import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [sensors, setSensors] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [selectedZone, setSelectedZone] = useState('AlmacÃ©n A');
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const res = await fetch('http://localhost:3000/api/sensors');
      const data = await res.json();
      setSensors(data);
    };

    const fetchAlerts = async () => {
      const res = await fetch('http://localhost:3000/api/alerts');
      const data = await res.json();
      setAlerts(data);
    };

    fetchData();
    fetchAlerts();
    const interval = setInterval(() => {
      fetchData();
      fetchAlerts();
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      const filtered = sensors
        .filter(s => ['Temp', 'Vib', 'Hum'].some(type => s.sensor_id.startsWith(type)))
        .slice(0, 3);
      setHistory(filtered);
    }, 30000);

    return () => clearInterval(interval);
  }, [sensors]);

  const filteredSensors = sensors.filter(s => s.zone === selectedZone);

  return (
    <div className="App">
      <header>
        <div className="logo-title">
          <img src="/logo192.png" alt="Logo" className="logo" />
          <h1>InnovaFactory Dashboard</h1>
        </div>
        <div className="user">ð¤ Usuario</div>
      </header>

      <div className="zone-selector">
        <label>Zona: </label>
        <select onChange={(e) => setSelectedZone(e.target.value)} value={selectedZone}>
          <option>AlmacÃ©n A</option>
          <option>Zona B</option>
          <option>Zona C</option>
          <option>Zona TÃ©cnica</option>
          <option>Motor A</option>
        </select>
      </div>

      <div className="sensor-carrusel">
        <div className="scrolling-strip">
          {filteredSensors.map((s, i) => (
            <div className={`sensor-card ${s.status === 'ALERT' ? 'alert' : ''}`} key={i}>
              <p>{s.sensor_id}</p>
              <h2>{s.value} {s.unit}</h2>
              <span className={`status ${s.status.toLowerCase()}`}>{s.status}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="alerts-section">
        <h2>ð¨ Alertas activas</h2>
        <ul>
          {alerts.map((alert, i) => (
            <li key={i}>
              ð´ {alert.sensor_id} en {alert.zone} â {alert.value}{alert.unit}
            </li>
          ))}
        </ul>
      </div>

      <div className="history-section">
        <h2>ð Historial de lecturas</h2>
        <table>
          <thead>
            <tr>
              <th>Sensor</th>
              <th>Valor</th>
              <th>Hora</th>
            </tr>
          </thead>
          <tbody>
            {history.map((row, i) => (
              <tr key={i}>
                <td>{row.sensor_id}</td>
                <td>{row.value} {row.unit}</td>
                <td>{new Date(row.timestamp).toLocaleTimeString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
